require("base.books")

module("content.books.founding_silverbrand", package.seeall)

-- This file contains the book data for the book "The founding of Silverbrand"

function loadBook(toData)
    base.books.AddLanguage("dwarf language",toData);
    
    base.books.AddEnglishBookText( "Within this book you will find the written speech of our great King, founding father of Silverbrand, on behalf of our town.", 0, 0,toData);
    base.books.AddEnglishBookText( "Dear citizens, allies and friends of Silverbrand,", 0, 0,toData);
    base.books.AddEnglishBookText( "Silverbrand was no gift from the gods, it's the result of hard work of the founder dwarves, who digged the first caverns with their own hands, who payed every brick, every anvil and every furnace", 0, 0,toData);
    base.books.AddEnglishBookText( "with their gold. This town was build using a total of more than 10.000 silvercoins (translated to current prices) which was payed by the founder dwarves alone.", 0, 0,toData); 
    base.books.AddEnglishBookText( "3/4 of this sum 10 dwarves payed and worked for all on their own, I myself have contributed about 5000 coins.", 0, 0,toData);
    base.books.AddEnglishBookText( "Rugosch, Bombor, Caranthir and Gandela contributed about 1000 silvercoins, each through his own hard work.", 0, 0,toData);
    base.books.AddEnglishBookText( "All founder dwarves agreed that every dwarf may find a home in this town if he is willing to proclaim the oath of trust.", 0, 0,toData); 
    base.books.AddEnglishBookText( "These dwarves, which buildings you take as if they'd been gifted by someone and which you use as if it'd be natural have decided that I should be king.", 0, 0,toData);
    base.books.AddEnglishBookText( "Bombor smithed my crown with his own hands, Rugosch as the highest priest of Irmorom in this lands crowned me during a big festivity. ", 0, 0,toData);
    base.books.AddEnglishBookText( "All dwarves that helped to build the town spoke their oath on myself, and thus elected myself to be the guardian of the finances, the lord and overseer over their work (the town) for my lifetime. ", 0, 0,toData); 
    base.books.AddEnglishBookText( "Even Irmoroms voice could be heard on the day of the coronation, blessing our kingdom and our work. ", 0, 0,toData);
    base.books.AddEnglishBookText( "I demand respect from all who use the gifts these fouder dwarves have brought to us and live in Silverbrand. Only the elves have done simmilar to the dwarves, creating a similar kingdom on their ", 0, 0,toData);
    base.books.AddEnglishBookText( "island. Nevertheless us dwarves were the first to have an own kingdom and that is one thing that shall never be forgotten. ", 0, 0,toData); 
    base.books.AddEnglishBookText( "And to stop the forgetting hear my voice and read my words. ", 0, 0,toData);
    base.books.AddEnglishBookText( "Never forget that it were 3 dwarves who sat at a campfire and decided: WE WILL BUILD A TOWN, FOR US DWARVES ONLY. These three whose idea incited a burning desire in ", 0, 0,toData);
    base.books.AddEnglishBookText( "the hearts of the dwarves were Bombor, Rugosch and Tialdin, and immediately, others followed: Caranthir, Gandela, Torekin, Galim, Belegi, Josef, Ingrain, Grimbart, Whisler, Rackere, Mishrak,", 0, 0,toData); 
    base.books.AddEnglishBookText( "Crotkin and many others but the ones listed where those who gave the most to help build this town.", 0, 0,toData);
    base.books.AddEnglishBookText( "Now I hear the question why there's an oath and why Silverbrand is a monarchy with a soveign wielding absolute power.", 0, 0,toData);
    base.books.AddEnglishBookText( "I answer this question: The founder dwarves wanted it to be like that. Every dwarf that comes to Silverbrand is a guest of those first dwarves, who have elected me to be their ", 0, 0,toData); 
    base.books.AddEnglishBookText( "king. Every dwarf that proclaims his oath, so these dwarves have decided, may call Silverbrand his home. ", 0, 0,toData);
    base.books.AddEnglishBookText( "The only demand they ask is respect for their work. Respect and obedience for the king. ", 0, 0,toData);
    base.books.AddEnglishBookText( "Who doesn't want to respect the founder dwarves should decide to find a new home, before he takes the oath. ", 0, 0,toData); 
    base.books.AddEnglishBookText( "Tialdin, King of Silverbrand ", 0, 0,toData);
    
    base.books.AddGermanBookText( "Im Folgenden befindet sich eine Mitschrift einer Rede unseres großen Kï¿½nigs und Grï¿½nders von Silberbrand zur Grï¿½ndung von Silberbrand:", 0, 0,toData);   
    base.books.AddGermanBookText( "Wehrte Untertanen, Verbï¿½ndete und Freunde Silberbrands, ", 0, 0,toData);
    base.books.AddGermanBookText( "Silberbrand wurde uns nicht von den Göttern geschenkt, es ist das Ergebnis harter Arbeit der Grï¿½nderzwerge, ", 0, 0,toData);      
    base.books.AddGermanBookText( "die mit ihren eigenen Hï¿½nden die Tunnel in den Fels trieben, jeden Stein, jeden Amboss und jede Esse mit ihrem Gold bezahlten. In dieser Stadt steckt nach damals gï¿½ltigen Preisen der Gegenwert von ", 0, 0,toData);   
    base.books.AddGermanBookText( "weit ï¿½ber 10.000 Silberbarren die die Grï¿½nderzwerge aufbrachten. Von diesen Mengen an Silber haben weniger als zehn Zwerge drei viertel alleine aufgebracht, ich selbst habe den Gegenwert von ", 0, 0,toData);
    base.books.AddGermanBookText( "ï¿½ber 5.000 Barren aufgebracht. Rugosch, Bombor, Caranthir und Gandela jeweils alleine weit ï¿½ber 1.000 durch Silber oder Arbeit. Alle Grï¿½nderzwerge haben entschieden, das sie jeden Zwerg dieses ", 0, 0,toData);   
    base.books.AddGermanBookText( "Kï¿½nigreich Heimat nennen lassen, der bereit ist den Treueeid abzulegen. Diese Zwerge deren Bauten ihr so selbstverstï¿½ndlich hinnehmt und nutzt haben mich zum Kï¿½nig erklï¿½rt. Bombor schmiedete meine ", 0, 0,toData);   
    base.books.AddGermanBookText( "Krone mit seinen eigenen Hï¿½nden, Rugosch als hï¿½chster Vertreter Irmoroms in diesen Landen setzte sie mir in einer feierlichen Zeremonie auf. Alle Zwerge die sich am Bau beteiligt hatten leisteten ", 0, 0,toData);
    base.books.AddGermanBookText( "mir ihren Treueeid und erklï¿½rten mich damit auf Lebenszeit zum Verwalter ihres Vermï¿½gens, zum Herrn und Hï¿½ter ï¿½ber ihrer Hände Arbeit. Irmoroms Stimme selbst erschallte am Tage der Krï¿½nung im ", 0, 0,toData);   
    base.books.AddGermanBookText( "Tempel und segnete unser Kï¿½nigreich unserer Hände Arbeit. ", 0, 0,toData);   
    base.books.AddGermanBookText( "Ich erwarte Respekt von jenen die die Gaben der Grï¿½nderzwerge nutzen von jenen die in Silberbrand leben. Einzig die Elfen haben es uns gleichgetan und ein ï¿½hnlich eindrucksvolles Reich auf ihrer ", 0, 0,toData);
    base.books.AddGermanBookText( "Insel geschaffen. Dennoch waren wir Zwerge es die als erste ein eigenes Reich hatten und das darf niemals vergessen werden. ", 0, 0,toData);   
    base.books.AddGermanBookText( "Um dem Vergessen Einhalt zu gebieten hï¿½ret meine Stimme und lest meine Worte. ", 0, 0,toData);   
    base.books.AddGermanBookText( "Niemals darf vergessen werden das es drei Zwerge waren die am Lagerfeuer saï¿½en und entschieden. WIR BAUEN UNS ZWERGEN EINE STADT, NUR Fï¿½R UNS ZWERGE. Diese drei deren Idee die Herzen der ", 0, 0,toData);
    base.books.AddGermanBookText( "Zwerge entflammten waren Bombor, Rugosch und Tialdin und es schlossen sich schnell weitere an: Caranthir, Gandela, Torekin, Galim, Belegi, Josef, Ingrain, Grimbart, Whisler, Rackere, Mishrak, ", 0, 0,toData);   
    base.books.AddGermanBookText( "Croktin und etliche mehr doch die ersten waren es die auch am meisten gaben um die Stadt zu bauen. ", 0, 0,toData);   
    base.books.AddGermanBookText( "Nunmehr hï¿½re ich die Frage warum ein Treueeid gefordert wird, warum es in Silberbrand einen Kï¿½nig gibt der ï¿½ber absolute Macht verfï¿½gt. Auf diese Frage antworte ich: Die Grï¿½ndervï¿½ter wollten es so. ", 0, 0,toData);
    base.books.AddGermanBookText( "Jeder Zwerg der Silberbrand betritt ist Gast der Grï¿½ndervï¿½ter, die mich zum Kï¿½nig und zu ihrem Verwalter machten. Jeder Zwerg der den Treueeid ablegt, so haben es die Grï¿½ndervï¿½ter entschieden, ", 0, 0,toData);      
    base.books.AddGermanBookText( "darf Silberbrand seine Heimat nennen. Die einzigen Forderungen die sie haben ist Respekt gegenï¿½ber ihrer Arbeit und Schaffenskraft. Respekt und Gehorsam gegenï¿½ber dem Kï¿½nig.", 0, 0,toData);   
    base.books.AddGermanBookText( "Wer den Grï¿½ndervï¿½tern diesen Respekt nicht entgegenbringen will, sollte sich noch bevor er seinen Eid ablegt entscheiden und sich eine andere Heimat suchen.", 0, 0,toData);
    base.books.AddGermanBookText( "Tialdin, Kï¿½nig von Silberbrand", 0, 0,toData);  
end;

function loadTitle(toData)
    base.books.AddGermanBookTitle("Buch mit dem Titel \"Von der Grï¿½ndung Silverbrands\"",toData);
    base.books.AddEnglishBookTitle("Book with the title \"The founding of Silverbrand\"",toData);
end;